mapper.select:
SELECT PRODUCT.PRODUCT_CODE,PRODUCT.PRODUCT_TITLE, PRODUCT.PRODUCT_DESC, PRODUCT.PRODUCT_HIT, 
PRODUCT.PRODUCT_IMG_DATA, PRODUCT.PRODUCT_IMG_TYPE,PRODUCT_OPTION.OPTION_PRICE FROM PRODUCT, PRODUCT_OPTION 
WHERE PRODUCT.PRODUCT_CODE= PRODUCT_OPTION.PRODUCT_CODE AND PRODUCT_TITLE LIKE '%' || #{title} || '%' ORDER BY OPTION_PRICE DESC;

SELECT PRODUCT.PRODUCT_CODE,PRODUCT.PRODUCT_TITLE, PRODUCT.PRODUCT_DESC, PRODUCT.PRODUCT_HIT, 
PRODUCT.PRODUCT_IMG_DATA, PRODUCT.PRODUCT_IMG_TYPE,PRODUCT_OPTION.OPTION_PRICE FROM PRODUCT, PRODUCT_OPTION 
WHERE PRODUCT.PRODUCT_CODE= PRODUCT_OPTION.PRODUCT_CODE AND PRODUCT_TITLE LIKE '%' || #{title} || '%' ORDER BY OPTION_PRICE ASC;
